// initialise the counter to 0 if it doesn't exist already
var count = context.get('count')||0;
var hardness = context.get('hardness')||0;
var randomness = context.get('randomness')||0;
count += 1;
// store the value back
context.set('count',count);
// make it part of the outgoing msg object
msg.payload.d.count = count;
if(count % 300 == 0) {
    context.set('randomness',1);
    context.set('hardness', 83);
} else if(count % 20 == 0) {
    context.set('randomness',0);
} else if(count % 121 == 0) {
    context.set('randomness',2);
    context.set('hardness', 100);
}

if(randomness == 0) {
    // no variation
    msg.payload.d.hardness = Math.floor(Math.random() * (80 - 70 + 1) + 70);
} else if(randomness == 1) {
    // gradually increase
    msg.payload.d.hardness = hardness + 1;
    context.set('hardness', (hardness + 1));
} else {
    msg.payload.d.hardness = hardness + 5;
    context.set('hardness', (hardness + 5));
}
msg.payload.d.temperature = Math.floor(Math.random() * (100 - 80 + 1) + 80);

//msg.payload = JSON.stringify(msg.payload)
msg.eventOrCommandType = "fluid"
return msg;

